/**
 * @Date:   2020-01-28T01:14:02+00:00
 * @Last modified time: 2020-02-11T15:28:39+00:00
 */



import React, { Component } from 'react';
import axios from 'axios';
import Form from 'react-bootstrap/Form'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Button from 'react-bootstrap/Button'
import InputGroup from 'react-bootstrap/InputGroup'
import Badge from 'react-bootstrap/Badge'


const Genre = props => (
  <Badge variant="light">{props.genre.name}</Badge>
)
const Author = props => (
  <Badge variant="light">{props.authors.name}</Badge>
)

export default class BookCreate extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isbn: '',
      title: '',
      genres: [],
      genre_id: [],
      authors: [],
      authors_id: []

    };

  }

  componentDidMount(){

    axios.defaults.headers.common['Authorization'] = localStorage.getItem('jwtToken');
    axios.get(`http://localhost:4000/books/authors`).then((result) => {
      console.log(result);
      this.setState({
        authors: result.data
      })
    })
      axios.get(`http://localhost:4000/books/genres`).then((genres) => {
        console.log(genres);
        this.setState({
          genres: genres.data
        })
    });
  };

  handleInputChange = e => {
    const target = e.target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    const name = target.name;

    console.log(`Input name ${name}. Input value ${value}.`);

    this.setState({
      [name]: value
    });
  };

  onSubmit = e => {
    e.preventDefault();

    let genreJSON = this.state.genres.map((name, index) => {
      return {name};
    })
    let authorJSON = this.state.authors.map((name, index) => {
      return {name};
    })

    const book = {
      isbn: this.state.isbn,
      title: this.state.title,
      genre: genreJSON,
      author: authorJSON,
      cover: this.state.cover
    }

    console.log(book);
    axios.defaults.headers.common['Authorization'] = localStorage.getItem('jwtToken')

    axios.post('http://localhost:4000/books/', book)
      .then(res => {
        console.log(res.data)
        window.location = '/';
      })
      .catch(err => {
        console.log(err)
      //  window.location = '/books/create';
    });

    // window.location = '/';
  };


  genreList() {
    return this.state.genres.map((currentGenre, index) => {
      return <Genre genre={currentGenre} key={index} />;
    })
  }
  authorList() {
    return this.state.authors.map((currentAuthor, index) => {
      return <Author author={currentAuthor} key={index} />;
    })
  }

  render() {

    return (
    <div>
      <h3>Add new Book</h3>
      <Form onSubmit={this.onSubmit}>
        <Form.Group as={Row} controlId="formHorizontalISBN">
          <Form.Label column sm={2}>
            ISBN
          </Form.Label>
          <Col sm={10}>
            <Form.Control type="text" placeholder="ISBN"
              name="isbn"
              value={this.state.isbn}
              onChange={this.handleInputChange}
            />
          </Col>
        </Form.Group>

        <Form.Group as={Row} controlId="formHorizontalTitle">
          <Form.Label column sm={2}>
            Title
          </Form.Label>
          <Col sm={10}>
            <Form.Control type="text" placeholder="Title"
              name="title"
              value={this.state.title}
              onChange={this.handleInputChange}
            />
          </Col>
        </Form.Group>

        <Form.Group as={Row} controlId="formHorizontalGenre">
          <Form.Label column sm={2}>
            Genres
          </Form.Label>
          <Col sm={4}>
            <InputGroup>
              <Form.Control type="text" placeholder="Genre"
                name="genreText"
                value={this.state.genreText}
                onChange={this.handleInputChange}
              />
              <InputGroup.Append>
                <Button onClick={this.onAddGenre} variant="outline-success">Add Genre</Button>
              </InputGroup.Append>
            </InputGroup>
          </Col>
        </Form.Group>
        <Row>
          <Col sm={{ span: 10, offset: 2 }}>
            { this.genreList() }
          </Col>
        </Row>

        <Form.Group as={Row} controlId="formHorizontalAuthor">
          <Form.Label column sm={2}>
            Author
          </Form.Label>
          <Col sm={4}>
            <InputGroup>
              <Form.Control type="text" placeholder="Author"
                name="authorText"
                value={this.state.authorText}
                onChange={this.handleInputChange}
              />
              <InputGroup.Append>
                <Button onClick={this.onAddAuthor} variant="outline-success">Add Author</Button>
              </InputGroup.Append>
            </InputGroup>
          </Col>
        </Form.Group>

        <Row>
          <Col sm={{ span: 10, offset: 2 }}>
            { this.authorList() }
          </Col>
        </Row>
        <Form.Group as={Row} controlId="formHorizontalover">
          <Form.Label column sm={2}>
            Cover
          </Form.Label>
          <Col sm={10}>
            <Form.Control type="file" placeholder=""
              name="Cover"

              onChange={this.onFileChange}
            />
          </Col>
        </Form.Group>
        <br/>
        <Form.Group as={Row}>
          <Col sm={{ span: 10, offset: 2 }}>
            <Button type="submit">Add Book</Button>
          </Col>
        </Form.Group>
      </Form>
    </div>
    )
  }
}
