/**
 * @Date:   2020-01-28T01:14:02+00:00
 * @Last modified time: 2020-02-11T17:56:06+00:00
 */

import React, { Component } from "react";
import axios from "axios";
import Form from "react-bootstrap/Form";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Button from "react-bootstrap/Button";
import InputGroup from "react-bootstrap/InputGroup";
import Badge from "react-bootstrap/Badge";

const Genre = props => (
  <option value={props.genre._id}> {props.genre.name} </option>
);
const Author = props => (
  <option value={props.author._id}>{props.author.name}</option>
);

export default class BookCreate extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isbn: "",
      title: "",
      genres: [],
      genre_id: [],
      authors: [],
      authors_id: []
    };
  }

  componentDidMount() {
    axios.defaults.headers.common["Authorization"] = localStorage.getItem(
      "jwtToken"
    );
    axios.get(`http://localhost:4000/authors`).then(authors => {
      console.log(authors);
      this.setState({
        authors: authors.data
      });
    });
    axios.get(`http://localhost:4000/genres`).then(genres => {
      console.log(genres);
      this.setState({
        genres: genres.data
      });
    });
  }

  handleInputChange = e => {
    const target = e.target;
    const value = target.type === "checkbox" ? target.checked : target.value;
    const name = target.name;

    console.log(`Input name ${name}. Input value ${value}.`);

    this.setState({
      [name]: value
    });
    console.log(this.state.author_id);
  };

  onAddAuthor = e => {
    const target = e.target;
    // const value = target.as === 'selected' ? target.selected : target.value;
    const name = target.name;

    var options = target.options;
    var value = [];
    for (var i = 0, l = options.length; i < l; i++) {
      if (options[i].selected) {
        value.push(options[i].value);
      }
    }
    //  this.props.someCallback(value);

    console.log(`Input name ${name}. Input value ${value}.`);

    this.setState({
      [name]: value
    });
  };

  addGenre = e => {
    const target = e.target;
    // const value = target.as === 'selected' ? target.selected : target.value;
    const name = target.name;

    var options = target.options;
    var value = [];
    for (var i = 0, l = options.length; i < l; i++) {
      if (options[i].selected) {
        value.push(options[i].value);
      }
    }
    //  this.props.someCallback(value);

    console.log(`Input name ${name}. Input value ${value}.`);

    this.setState({
      [name]: value
    });
  };

  onSubmit = e => {
    e.preventDefault();
    console.log(this.state.author_id);
    let genreJSON = this.state.genre_id;
    let authorJSON = this.state.author_id;

    const book = {
      isbn: this.state.isbn,
      title: this.state.title,
      genre_id: genreJSON,
      author_id: authorJSON
    };

    console.log(book);
    axios.defaults.headers.common["Authorization"] = localStorage.getItem(
      "jwtToken"
    );

    axios
      .post("http://localhost:4000/books", book)
      .then(res => {
        console.log(res.data);
        window.location = "/";
      })
      .catch(err => {
        console.log(err);
        //  window.location = '/books/create';
      });

    // window.location = '/';
  };

  genreList() {
    return this.state.genres.map((currentGenre, index) => {
      return <Genre genre={currentGenre} key={index} />;
    });
  }
  authorList() {
    return this.state.authors.map((currentAuthor, index) => {
      return <Author author={currentAuthor} key={index} />;
    });
  }

  render() {
    return (
      <div>
        <h3>Add new Book</h3>
        <Form onSubmit={this.onSubmit} encType="multipart/form-data">
          <Form.Group as={Row} controlId="formHorizontalISBN">
            <Form.Label column sm={2}>
              ISBN
            </Form.Label>
            <Col sm={10}>
              <Form.Control
                type="text"
                placeholder="ISBN"
                name="isbn"
                value={this.state.isbn}
                onChange={this.handleInputChange}
              />
            </Col>
          </Form.Group>

          <Form.Group as={Row} controlId="formHorizontalTitle">
            <Form.Label column sm={2}>
              Title
            </Form.Label>
            <Col sm={10}>
              <Form.Control
                type="text"
                placeholder="Title"
                name="title"
                value={this.state.title}
                onChange={this.handleInputChange}
              />
            </Col>
          </Form.Group>

          <Form.Group as={Row} controlId="formHorizontalGenre">
            <Form.Label column sm={2}>
              Genre
            </Form.Label>
            <Col sm={4}>
              <InputGroup>
                <Form.Control
                  as="select"
                  placeholder="Genre"
                  name="genre_id"
                  onChange={this.addGenre}
                >
                  {this.genreList()}
                </Form.Control>
              </InputGroup>
            </Col>
          </Form.Group>

          <Form.Group
            as={Row}
            controlId="formHorizontalAuthor"
            className="custome-file"
          >
            <Form.Label column sm={2}>
              Cover Upload
            </Form.Label>
            <Col sm={8}>
              {/* <InputGroup> */}
              <Form.Control
                type="file"
                placeholder="cover"
                className="custom-file-input"
                name="cover"
                onChange={this.cover}
              ></Form.Control>
              {/* </InputGroup> */}
              {/* <div class="custom-file"> */}
              <input
                type="file"
                class="custom-file-input"
                id="validatedCustomFile"
                required
              />
              <label class="custom-file-label" for="validatedCustomFile">
                Choose file...
              </label>
              <div class="invalid-feedback">
                Example invalid custom file feedback
              </div>
              {/* </div> */}
            </Col>
          </Form.Group>

          <br />
          <Form.Group as={Row}>
            <Col sm={{ span: 10, offset: 2 }}>
              <Button type="submit">Add Book</Button>
            </Col>
          </Form.Group>
        </Form>
      </div>
    );
  }
}
