/**
 * @Date:   2020-01-28T11:34:07+00:00
 * @Last modified time: 2020-02-03T14:29:23+00:00
 */
import React, { Component } from "react";
import axios from "axios";
import Form from "react-bootstrap/Form";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Button from "react-bootstrap/Button";
import InputGroup from "react-bootstrap/InputGroup";

export default class Login extends Component {
  constructor(props) {
    super(props);

    this.state = {
      email: "",
      password: ""
    };
  }

  handleInputChange = e => {
    const target = e.target;
    const value = target.type === "checkbox" ? target.checked : target.value;
    const name = target.name;

    console.log(`Input name ${name}. Input value ${value}.`);

    this.setState({
      [name]: value
    });
  };

  onSubmit = e => {
    e.preventDefault();

    const user = {
      email: this.state.email,
      password: this.state.password
    };

    console.log(user);

    axios
      .post("http://localhost:4000/account/login", user)
      .then(res => {
        // save token in local storage
        localStorage.setItem("jwtToken", res.data.token);
        this.props.onLogin();
        console.log(res.data);
        window.location = "books/create";
      })
      .catch(err => {
        if (err.response.status === 401) {
          this.setState({
            message: "Login failed. Username or password not match"
          });
        }
      });
  };

  render() {
    return (
      <div>
        <h3>Login</h3>
        <Form onSubmit={this.onSubmit}>
          <InputGroup as={Row} controlId="formHorizontalIMDB">
            <Form.Label column sm={2}>
              Email
            </Form.Label>
            <Col sm={10}>
              <InputGroup.Prepend>
                <InputGroup.Text id="basic-addon1">@</InputGroup.Text>
              </InputGroup.Prepend>
              <Form.Control
                type="email"
                placeholder="Email"
                name="email"
                value={this.state.email}
                onChange={this.handleInputChange}
                aria-label="Username"
                aria-describedby="basic-addon1"
              />
            </Col>
          </InputGroup>

          <Form.Group as={Row} controlId="formHorizontalTitle">
            <Form.Label column sm={2}>
              Password
            </Form.Label>
            <Col sm={10}>
              <Form.Control
                type="password"
                placeholder="Password"
                name="password"
                value={this.state.password}
                onChange={this.handleInputChange}
              />
            </Col>
          </Form.Group>

          <Form.Group as={Row}>
            <Col sm={{ span: 10, offset: 2 }}>
              <Button type="submit">Login</Button>
            </Col>
          </Form.Group>
        </Form>
      </div>
    );
  }
}
