/**
 * @Date:   2020-01-28T01:14:02+00:00
 * @Last modified time: 2020-02-11T15:41:54+00:00
 */



import React, { Component } from 'react';
import axios from 'axios';
import Form from 'react-bootstrap/Form'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Button from 'react-bootstrap/Button'
import InputGroup from 'react-bootstrap/InputGroup'
import Badge from 'react-bootstrap/Badge'


const Genre = props => (
  <option value={props.genre._id}> {props.genre.name} </option>
)
const Author = props => (
  <option value={props.author._id}>{props.author.name}</option>
)

export default class BookCreate extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isbn: '',
      title: '',
      genres: [],
      genre_id: [],
      authors: [],
      authors_id: []

    };

  }

  componentDidMount(){

    axios.defaults.headers.common['Authorization'] = localStorage.getItem('jwtToken');
    axios.get(`http://localhost:4000/books/authors`).then((result) => {
      console.log(result);
      this.setState({
        authors: result.data
      })
    })
      axios.get(`http://localhost:4000/books/genres`).then((genres) => {
        console.log(genres);
        this.setState({
          genres: genres.data
        })
    });
  };

  handleInputChange = e => {
    const target = e.target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    const name = target.name;

    console.log(`Input name ${name}. Input value ${value}.`);

    this.setState({
      [name]: value
    });
    console.log(this.state.author_id)
  };

  onSubmit = e => {
    e.preventDefault();

    let genreJSON = this.state.genres.map((name, index) => {
      return {name};
    })
    let authorJSON = this.state.authors.map((name, index) => {
      return {name};
    })

    const book = {
      isbn: this.state.isbn,
      title: this.state.title,
      genre: genreJSON,
      author: authorJSON,
      cover: this.state.cover
    }

    console.log(book);
    axios.defaults.headers.common['Authorization'] = localStorage.getItem('jwtToken')

    axios.post('http://localhost:4000/books/', book)
      .then(res => {
        console.log(res.data)
        window.location = '/';
      })
      .catch(err => {
        console.log(err)
      //  window.location = '/books/create';
    });

    // window.location = '/';
  };


  genreList() {
    return this.state.genres.map((currentGenre, index) => {
      return <Genre genre={currentGenre} key={index} />;
    })
  }
  authorList() {
    return this.state.authors.map((currentAuthor, index) => {
      return <Author author={currentAuthor} key={index} />;
    })
  }

  render() {

    return (
    <div>
      <h3>Add new Book</h3>
      <Form onSubmit={this.onSubmit}>
        <Form.Group as={Row} controlId="formHorizontalISBN">
          <Form.Label column sm={2}>
            ISBN
          </Form.Label>
          <Col sm={10}>
            <Form.Control type="text" placeholder="ISBN"
              name="isbn"
              value={this.state.isbn}
              onChange={this.handleInputChange}
            />
          </Col>
        </Form.Group>

        <Form.Group as={Row} controlId="formHorizontalTitle">
          <Form.Label column sm={2}>
            Title
          </Form.Label>
          <Col sm={10}>
            <Form.Control type="text" placeholder="Title"
              name="title"
              value={this.state.title}
              onChange={this.handleInputChange}
            />
          </Col>
        </Form.Group>

        <Form.Group as={Row} controlId="formHorizontalGenre">
          <Form.Label column sm={2}>
            Genre
          </Form.Label>
          <Col sm={4}>
            <InputGroup>
              <Form.Control as="select" multiple placeholder="Genre"
                name="genre_id"
                value={this.state.genre_id}
                onChange={this.handleInputChange}
              >

                { this.genreList() }

              </Form.Control>
            </InputGroup>
          </Col>
        </Form.Group>

        <Form.Group as={Row} controlId="formHorizontalAuthor">
          <Form.Label column sm={2}>
            Author
          </Form.Label>
          <Col sm={4}>
            <InputGroup>
              <Form.Control as="select" multiple placeholder="Author"
                name="author_id"
                value={this.state.author}
                onChange={this.handleInputChange}
              >

        { this.authorList() }

              </Form.Control>
            </InputGroup>
          </Col>
        </Form.Group>


        <Form.Group as={Row} controlId="formHorizontalover">
          <Form.Label column sm={2}>
            Cover
          </Form.Label>
          <Col sm={10}>
            <Form.Control type="file" placeholder=""
              name="Cover"

              onChange={this.onFileChange}
            />
          </Col>
        </Form.Group>
        <br/>
        <Form.Group as={Row}>
          <Col sm={{ span: 10, offset: 2 }}>
            <Button type="submit">Add Book</Button>
          </Col>
        </Form.Group>
      </Form>
    </div>
    )
  }
}
